import React, { useState } from 'react';
import DefectSelection from './DefectSelection';

const VisualDimensionalCheck = ({ heatNumber, productType, onResultChange }) => {
    const [dimensionalReadings, setDimensionalReadings] = useState(Array(20).fill(''));
    const [isHeatRejected, setIsHeatRejected] = useState(false);



    const checkDimensionalRejection = (readings) => {
        // Basic logic: if more than 1 reading outside tolerance -> reject
        // Use simple tolerance based on productType (example ranges)
        let min = 20.47, max = 23.23;
        if (productType && productType.toLowerCase().includes('mk iii')) {
            min = 20.47; max = 20.84;
        } else if (productType && productType.toLowerCase().includes('mk v')) {
            min = 22.81; max = 23.23;
        }
        const outOfRange = readings.reduce((acc, r) => {
            const v = parseFloat(r);
            if (!Number.isFinite(v)) return acc;
            return acc + (v < min || v > max ? 1 : 0);
        }, 0);
        const reject = outOfRange > 1;
        setIsHeatRejected(reject);
        onResultChange && onResultChange({ heatNumber, rejected: reject, readings });
    };

    const handleDefectRejection = (isRejected) => {
        setIsHeatRejected(isRejected);
        onResultChange && onResultChange({ heatNumber, rejected: isRejected, readings: dimensionalReadings });
    };

    const handleDimensionalChange = (index, value) => {
        const newReadings = [...dimensionalReadings];
        newReadings[index] = value;
        setDimensionalReadings(newReadings);
        checkDimensionalRejection(newReadings);
    };

    return (
        <div>
            <h3>Visual & Dimensional Check for Heat #{heatNumber}</h3>
            <DefectSelection onRejection={handleDefectRejection} />
            <hr />
            <h3>Dimensional Check</h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: 8 }}>
                {dimensionalReadings.map((val, idx) => (
                    <input key={idx} className="form-control" placeholder={`#${idx + 1}`} value={val} onChange={(e) => handleDimensionalChange(idx, e.target.value)} />
                ))}
            </div>
            {isHeatRejected && <p style={{ color: 'red' }}>This heat is REJECTED.</p>}
        </div>
    );
};

export default VisualDimensionalCheck;
