import React, { useState, useEffect } from 'react';

const DEFAULT_RANGES = {
  C: [0.5, 0.6],
  Si: [1.5, 2.0],
  Mn: [0.8, 1.0],
  P: [0, 0.03],
  S: [0, 0.03],
};

const MaterialTesting = ({ heatNumber, onResultChange }) => {
  const [values, setValues] = useState({ C: '', Si: '', Mn: '', P: '', S: '', hardness: '', grainSize: '', decarbDepth: '' });
  const [rejected, setRejected] = useState(false);

  useEffect(() => {
    validateValues(values);
  }, [values]);

  const validateValues = (vals) => {
    let isRejected = false;
    Object.keys(DEFAULT_RANGES).forEach((k) => {
      const v = parseFloat(vals[k]);
      if (!Number.isFinite(v)) return;
      const [min, max] = DEFAULT_RANGES[k];
      if (v < min || v > max) isRejected = true;
    });
    // additional checks
    const decarb = parseFloat(vals.decarbDepth);
    if (Number.isFinite(decarb) && decarb > 0.25) isRejected = true;

    setRejected(isRejected);
    onResultChange && onResultChange({ heatNumber, rejected: isRejected, values: vals });
  };

  const handleChange = (field, v) => {
    setValues(prev => ({ ...prev, [field]: v }));
  };

  return (
    <div style={{ padding: '12px', border: '1px solid var(--color-border)', borderRadius: 6, marginBottom: '12px' }}>
      <h4>Material Testing - Heat #{heatNumber}</h4>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 8 }}>
        <label>
          %C
          <input className="form-control" type="number" step="0.01" value={values.C} onChange={e => handleChange('C', e.target.value)} />
        </label>
        <label>
          %Si
          <input className="form-control" type="number" step="0.01" value={values.Si} onChange={e => handleChange('Si', e.target.value)} />
        </label>
        <label>
          %Mn
          <input className="form-control" type="number" step="0.01" value={values.Mn} onChange={e => handleChange('Mn', e.target.value)} />
        </label>
        <label>
          %P
          <input className="form-control" type="number" step="0.001" value={values.P} onChange={e => handleChange('P', e.target.value)} />
        </label>
        <label>
          %S
          <input className="form-control" type="number" step="0.001" value={values.S} onChange={e => handleChange('S', e.target.value)} />
        </label>
        <label>
          Hardness (HRC)
          <input className="form-control" type="number" step="0.1" value={values.hardness} onChange={e => handleChange('hardness', e.target.value)} />
        </label>
        <label>
          Grain Size
          <input className="form-control" type="number" step="1" value={values.grainSize} onChange={e => handleChange('grainSize', e.target.value)} />
        </label>
        <label>
          Depth of Decarb (mm)
          <input className="form-control" type="number" step="0.01" value={values.decarbDepth} onChange={e => handleChange('decarbDepth', e.target.value)} />
        </label>
      </div>
      {rejected ? <div className="alert alert-error" style={{ marginTop: 8 }}>This heat is REJECTED based on material test values.</div> : <div style={{ marginTop: 8 }} className="alert alert-success">Material test within limits (so far)</div>}
    </div>
  );
};

export default MaterialTesting;
