import React, { useState, useMemo } from 'react';
import DefectSelection from './DefectSelection';

const VisualDimensionalCheck = ({ heatNumber, productType, onResultChange, inspectionStage }) => {
    // Hooks must be called unconditionally at the top of the component
    // Each sample holds { length, width, height, weight }
    const [samples, setSamples] = useState(Array.from({ length: 20 }, () => ({ length: '', width: '', height: '', weight: '' })));
    const [isHeatRejected, setIsHeatRejected] = useState(false);

    // Only applicable for ERC Raw Material inspection stage — render a notice early,
    // but hooks are already registered above to satisfy the rules of hooks.
    if (inspectionStage !== 'ERC-Raw') {
        return (
            <div style={{ padding: 12, border: '1px dashed var(--color-border)', borderRadius: 6 }}>
                <h4>Visual & Dimensional Check</h4>
                <p style={{ margin: 0 }}>This check applies only for ERC Raw Material inspections.</p>
            </div>
        );
    }



    const checkDimensionalRejection = (samplesArr) => {
        // Basic logic: if more than 1 sample has ANY of length/width/height outside tolerance -> reject
        // Tolerances vary by product type; default generic tolerance used here
        let lengthRange = [20.47, 23.23];
        // Example overrides for named product types
        if (productType && /mk\s*iii/i.test(productType)) {
            lengthRange = [20.47, 20.84];
        } else if (productType && /mk\s*v/i.test(productType)) {
            lengthRange = [22.81, 23.23];
        }

        const outOfSpecSamples = samplesArr.reduce((acc, s) => {
            const l = parseFloat(s.length);
            const w = parseFloat(s.width);
            const h = parseFloat(s.height);
            const lBad = Number.isFinite(l) && (l < lengthRange[0] || l > lengthRange[1]);
            // Width/Height use a generic small tolerance around length, e.g., ±0.5mm
            const wBad = Number.isFinite(w) && (w < (lengthRange[0] - 0.5) || w > (lengthRange[1] + 0.5));
            const hBad = Number.isFinite(h) && (h < (lengthRange[0] - 0.5) || h > (lengthRange[1] + 0.5));
            return acc + ((lBad || wBad || hBad) ? 1 : 0);
        }, 0);

        const reject = outOfSpecSamples > 1;
        setIsHeatRejected(reject);
        onResultChange && onResultChange({ heatNumber, rejected: reject, samples: samplesArr });
    };

    const handleDefectRejection = (isRejected) => {
        setIsHeatRejected(isRejected);
        onResultChange && onResultChange({ heatNumber, rejected: isRejected, samples });
    };

    const handleSampleChange = (index, field, value) => {
        const next = samples.map((s, i) => i === index ? { ...s, [field]: value } : s);
        setSamples(next);
        checkDimensionalRejection(next);
    };

    // Derived statuses per sample
    const sampleStatuses = useMemo(() => {
        let lengthRange = [20.47, 23.23];
        if (productType && /mk\s*iii/i.test(productType)) lengthRange = [20.47, 20.84];
        if (productType && /mk\s*v/i.test(productType)) lengthRange = [22.81, 23.23];
        return samples.map(s => {
            const l = parseFloat(s.length);
            const w = parseFloat(s.width);
            const h = parseFloat(s.height);
            const validL = !Number.isFinite(l) ? null : (l >= lengthRange[0] && l <= lengthRange[1]);
            const validW = !Number.isFinite(w) ? null : (w >= (lengthRange[0] - 0.5) && w <= (lengthRange[1] + 0.5));
            const validH = !Number.isFinite(h) ? null : (h >= (lengthRange[0] - 0.5) && h <= (lengthRange[1] + 0.5));
            const anyFalse = [validL, validW, validH].some(v => v === false);
            const allNull = [validL, validW, validH].every(v => v === null);
            if (allNull) return 'Pending';
            return anyFalse ? 'Invalid' : 'Valid';
        });
    }, [samples, productType]);

    return (
        <div>
            <h3>Visual & Dimensional Check for Heat #{heatNumber}</h3>
            <DefectSelection onRejection={handleDefectRejection} />
            <hr />
            <h3>Dimensional Measurements (Sample Grid)</h3>
            <div style={{ overflowX: 'auto' }}>
                <table className="data-table" style={{ width: '100%' }}>
                    <thead>
                        <tr>
                            <th>Sample No.</th>
                            <th>Length (mm)</th>
                            <th>Width (mm)</th>
                            <th>Height (mm)</th>
                            <th>Weight (kg)</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {samples.slice(0, 5).map((s, idx) => (
                            <tr key={idx}>
                                <td style={{ width: 60 }}>{idx + 1}</td>
                                <td><input className="form-control" type="number" step="0.01" value={s.length} onChange={(e) => handleSampleChange(idx, 'length', e.target.value)} /></td>
                                <td><input className="form-control" type="number" step="0.01" value={s.width} onChange={(e) => handleSampleChange(idx, 'width', e.target.value)} /></td>
                                <td><input className="form-control" type="number" step="0.01" value={s.height} onChange={(e) => handleSampleChange(idx, 'height', e.target.value)} /></td>
                                <td><input className="form-control" type="number" step="0.01" value={s.weight} onChange={(e) => handleSampleChange(idx, 'weight', e.target.value)} /></td>
                                <td><span className={`status-badge ${sampleStatuses[idx] === 'Valid' ? 'valid' : sampleStatuses[idx] === 'Invalid' ? 'invalid' : 'pending'}`}>{sampleStatuses[idx]}</span></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            <div style={{ fontSize: '12px', color: 'var(--color-text-secondary)', marginTop: 8 }}>20 samples total per heat - showing first 5</div>

            <h4 style={{ marginTop: 18 }}>Visual Defects Checklist</h4>
            <DefectSelection onRejection={handleDefectRejection} />
            <div style={{ marginTop: 12 }}>
                {(() => {
                    const anyDefect = Object.values(samples).some((_, i) => false); // sample-level defects not tracked here
                    // use defect selection from the child via isHeatRejected for message
                    if (!isHeatRejected) return <div className="alert alert-success">✓ No defects found in current sample batch</div>;
                    return <div className="alert alert-error">✗ Defects found — heat rejected</div>;
                })()}
            </div>
        </div>
    );
};

export default VisualDimensionalCheck;
