import React, { useState, useMemo } from 'react';

const FinalInspectionResults = ({ heats, visualResults = {}, materialResults = {}, weights = {}, onRemarksChange }) => {
  // heats: array of heat ids (strings)
  const rows = useMemo(() => heats.map(h => {
    const v = visualResults[h];
    const m = materialResults[h];
    const rejected = (v && v.rejected) || (m && m.rejected) || false;
    return { heat: h, rejected, weight: weights[h] || '', remarks: '' };
  }), [heats, visualResults, materialResults, weights]);

  const [localRemarks, setLocalRemarks] = useState({});

  const handleRemark = (heat, text) => {
    setLocalRemarks(prev => ({ ...prev, [heat]: text }));
    onRemarksChange && onRemarksChange(heat, text);
  };

  return (
    <div>
      <h4>Final Inspection Results (Auto-Populated)</h4>
      <div style={{ overflowX: 'auto' }}>
        <table className="data-table">
          <thead>
            <tr>
              <th>Heat No.</th>
              <th>Accepted / Rejected</th>
              <th>Weight of Material (Auto)</th>
              <th>Remarks (Manual Entry, Required)</th>
            </tr>
          </thead>
          <tbody>
            {rows.map(r => (
              <tr key={r.heat}>
                <td><strong>{r.heat}</strong></td>
                <td>{r.rejected ? <span style={{ color: 'var(--color-error)', fontWeight: 'var(--font-weight-medium)' }}>Rejected</span> : <span style={{ color: 'var(--color-success)', fontWeight: 'var(--font-weight-medium)' }}>Accepted</span>}</td>
                <td>{r.weight || '—'}</td>
                <td>
                  <input type="text" className="form-control" value={localRemarks[r.heat] || ''} onChange={(e) => handleRemark(r.heat, e.target.value)} placeholder="Enter remarks..." />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default FinalInspectionResults;
